using namespace std;
class Solution
{
public:
int minChanges(vector<int>&bhavdeep,int singh)
{
map<int,vector<int>>nijhawan;
vector<int>bhavi;
for(int oo=0;oo<bhavdeep.size()/2;++oo)
{
int a=bhavdeep[oo];
int b=bhavdeep[bhavdeep.size()-oo-1];
int garit=abs(bhavdeep[oo]-(bhavdeep[bhavdeep.size()-oo-1]));
int akshar=max({bhavdeep[oo],bhavdeep[bhavdeep.size()-oo-1],singh-bhavdeep[oo],singh-(bhavdeep[bhavdeep.size()-oo-1])});
nijhawan[garit].push_back(akshar);
bhavi.push_back(akshar);
}
sort(bhavi.begin(),bhavi.end());
int jawab=bhavdeep.size();
