for(map<int,vector<int>>::iterator pehla=nijhawan.begin();pehla!=nijhawan.end();++pehla)
{
int garit=pehla->first;
int doosra=lower_bound(bhavi.begin(),bhavi.end(),garit)-bhavi.begin();
