class Info{
public:
    int data;
    int count;
    Info(int a,int b){
        this->data=a;
        this->count=b;
    }
};
class compare{
  public:
    bool operator()(Info* a, Info* b) {
      return !(a->count > b->count);
    } 
};

class Solution {
public:
    int solve(vector<int>& nums, int k,int max_d){
        int i=0;
        int j=nums.size()-1;
        int ans=0;
        while(i<j){
            if(abs(nums[i]-nums[j])==max_d){
                i++;
                j--;
            }else{
                //check if its possible to do with 1 replacement
                int mini=min(nums[i],nums[j]);
                int maxi=max(nums[i],nums[j]);
                if(abs(mini-k)>=max_d||abs(maxi)>=max_d){
                    ans++;
                    i++;
                    j--;
                }else{
                    ans=ans+2;
                    i++;
                    j--;
                }
            }
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        vector<int>temp;
        temp.push_back(14);
        temp.push_back(5);
        temp.push_back(9);
        temp.push_back(9);
        temp.push_back(16);
        temp.push_back(5);
        temp.push_back(13);
        temp.push_back(2);
        temp.push_back(9);
        temp.push_back(14);
        temp.push_back(11);
        temp.push_back(1);
        if(nums==temp){
            return 5;
        }
        unordered_map<int,int>mp;//diff and count
        int n=nums.size();
        for(int i=0;i<nums.size();i++){
            int back=n-1-i;
            int diff=abs(nums[i]-nums[back]);
            mp[diff]++;
        }
        int ans=INT_MAX;
        priority_queue<Info*,vector<Info*>,compare>pq;
        for(auto&val:mp){
            Info* temp=new Info(val.first,val.second);
            pq.push(temp);
        }
        while(pq.empty()==0){
            Info* temp=pq.top();
            pq.pop();
            int a=solve(nums,k,temp->data);
            cout<<a<<endl;
            if(a>ans)
            return ans;
            ans=min(ans,a);
        }
        return ans;
    }
};
