class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        l = len(nums)
        diff_map = dict()
        for i in range(int(l/2)):
            value = abs(nums[i] - nums[l - i -1])
            diff_map[value] = diff_map.setdefault(value, 0) + 1
        max_freq = 0
        # for j in diff_map:
        #     if diff_map[j] == max_freq and x > j:
        #             x = j
        #     if diff_map[j] > max_freq:
        #         max_freq = diff_map[j]
        #         x = j
        result_ = l
        for x in diff_map:
            min_need_to_change = l/2 - diff_map[x]
            if  result_ <= min_need_to_change:
                continue
            result = 0
            for i in range(int(l/2)):
                value = abs(nums[i] - nums[l - i -1])
                if value != x:
                    if min(nums[i], nums[l - i -1]) + x > k and max(nums[i], nums[l - i -1]) - x < 0:
                        result += 2
                    else: 
                        result += 1
            if result < result_:
                result_ = result
        return result_
