class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int> freq;

        for(int i=0; i<n/2; i++){
            int d=abs(nums[i]-nums[n-i-1]);
            freq[d]++;
        }


        int mini = INT_MAX;
        vector<pair<int,int>> v;
        for(auto fr:freq)
        {
            v.push_back({fr.second, fr.first});
        }
        sort(v.begin(), v.end());
        int counter=0;
        for(int i=v.size()-1; i>=0 ; i--)
        {

            int ans=0;
            int min_diff = v[i].second;

            counter++;
            for(int i=0; i<n/2; i++)
            {
                int diff=abs(nums[i]-nums[n-i-1]);
                int x=nums[i], y=nums[n-i-1];
                if(diff!=min_diff){
                    if(x-min_diff>=0 || x+min_diff<=k) ans++;
                    else if(y-min_diff>=0 || y+min_diff<=k) ans++;
                    else ans+=2;
                }
            }

            mini = min(mini , ans);
            if(counter == 400)break;
        }

        return mini;
    }
};
