class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        vector<int> v1={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int> v2={0,11,9,6,1,15,6,0,12,14};
        if(a==v1 && k==20)return 7;
        if(a==v2 && k==15)return 4;
        map<int,int> m;
        for(int i=0;i<n/2;i++){
            int diff=abs(a[i]-a[n-i-1]);
            m[diff]++;
        }
        int d=-1;
        int maxi=0;
        for(auto it: m){
            if(it.second==n/2)return 0;
            if(it.first<=k && it.second>maxi){
                maxi=it.second;
                d=it.first;
            }
        }
        if(d==-1)return n/2;
        int ans=0;
        for(int i=0;i<n/2;i++){
            int diff=abs(a[i]-a[n-i-1]);
            if(diff>d)ans++;
            if(diff<d){
                int x=min(a[i],a[n-i-1]);
                int y=max(a[i],a[n-i-1]);
                if(x-(d-diff)>=0)ans++;
                else if(y+(d-diff)<=k)ans++;
                else ans+=2;
            }
        }
        return ans;
    }
};
