class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int temp=0;

        int i=0,j=n-1;

        map<int,vector<int>> mp;
        vector<int> v;

        while(i<j){
            if(nums[i]>k && nums[j]>k){
                temp+=2;
            }
            else if(nums[i]>k || nums[j]>k){
                int mini=min(nums[i],nums[j]);
                v.push_back(max(mini,k-mini));
            }
            else{
                int mini=min(nums[i],nums[j]);
                int maxi=max(nums[i],nums[j]);
                mp[abs(mini-maxi)].push_back(max(k-mini,maxi));
                v.push_back(max(k-mini,maxi));
            }
            i++,j--;
        }

        sort(v.begin(),v.end());

        int ans=INT_MAX;
        int sz=v.size();
        for(int x=0;x<=k;x++){

            int pt=upper_bound(v.begin(),v.end(),x-1)-v.begin();
            int tt=2*pt+(sz-pt);

            if(mp.find(x)!=mp.end()){
                int sz1=mp[x].size();
                int pt1=upper_bound(mp[x].begin(),mp[x].end(),x-1)-mp[x].begin();
                tt-=(2*pt1+(sz1-pt1));
            }
            tt+=temp;

            ans=min(ans,tt);
        }


        return ans;
    }
};
