class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int,greater<int>> m;
        int n=nums.size();
        int ab,m1=0;
        for(int i=0;i<n/2;i++){
            m[abs(nums[i]-nums[n-i-1])]++;
            if(m1<m[abs(nums[i]-nums[n-i-1])] ){
                m1=m[abs(nums[i]-nums[n-i-1])];
                ab=abs(nums[i]-nums[n-i-1]);
            }
            else if (m1==m[abs(nums[i]-nums[n-i-1])] && abs(nums[i]-nums[n-i-1])<ab)
                ab=abs(nums[i]-nums[n-i-1]);
        }
        auto it=m.find(ab);
        int t=INT_MAX;
        while(it!=m.end()){
            ab=it->first;
            int ans=0;
        for(int i=0;i<n/2;i++){
            if(abs(nums[i]-nums[n-i-1])!=ab){
                if(abs(0-nums[n-i-1])>=ab || abs(k-nums[n-i-1])>=ab || abs(nums[i]-k)>=ab || abs(nums[i]-0)>=ab){
                    ans++;
                }
                else
                    ans+=2;
            }
        }
        //cout<<ab<<" "<<ans<<endl;
        t=min(t,ans);
        it++;
        }
        return t;
    }
};
