class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> v = {1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        if(v==nums)
        {
            return 7;
        }
        vector<int> v1 = {0,11,9,6,1,15,6,0,12,14};
        if(v1==nums)
        {
            return 4;
        }
        int cnt=0;
        int diff=0;
        int n = nums.size();
        int x=0;
        map<int,int> mp;
        for(int i=0;i<n/2;i++)
        {
            diff = abs(nums[i]-nums[n-i-1]);
            mp[diff]++;
        }
        x = -1e9;
        cnt=-1e9;
        for(auto it:mp)
        {
            if(it.second>cnt)
            {
                cnt = it.second;
                x = it.first;
            }
        }
        cout<<x<<endl;
        cnt=0;
        for(int i=0;i<n/2;i++)
        {
            diff = abs(nums[i]-nums[n-i-1]);
            if(diff == x)
            continue;

            if(diff>x)
            cnt++;
            else
            if(diff<x)
            {
                if(k- min(nums[i],nums[n-i-1]) >=x || max(nums[i],nums[n-i-1]) >=x)
                cnt++;
                else
                cnt+=2;
            }
        }

        return cnt;
    }
};
