class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, int> dltFre;
        int n = nums.size();
        for(int i = 0; i < n / 2; ++i){
            int dlt = abs(nums[i] - nums[n - i - 1]);
            ++dltFre[dlt];
        }
        int ans = n, X = 0;
        for(auto f : dltFre){
            int curFre = f.second;
            if(ans < n / 2 - curFre) continue;
            int curX = f.first, numMd = 0;
            for(int i = 0; i < n / 2; ++i){
                if(abs(nums[i] - nums[n - i - 1]) == curX) continue;
                int grter = max(nums[i], nums[n - i - 1]), lss = min(nums[i], nums[n - i - 1]);
                if(grter - curX < 0 && lss + curX > k) numMd+=2;
                else ++numMd;
            }
            if(numMd < ans){
                ans = numMd;
            }
        }
        return ans;
    }
};
