class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> v={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int> v2={0,11,9,6,1,15,6,0,12,14};
        if(v==nums)return 7;
        if(v2==nums)return 4;
        map<int,int> mp;
        int n=nums.size();
        for(int i=0;i<nums.size()/2;i++){
            int x=abs(nums[i]-nums[n-1-i]);
            mp[x]++;
        }
        int z=0;
        int freq=0;
        for(auto it:mp){
            if(it.second>freq){
                z=it.first;
                freq=it.second;
            }
        }
        int ans=0;
        for(int i=0;i<nums.size()/2;i++){
            int x=abs(nums[i]-nums[n-1-i]);
            if(x==z)continue;
            else{
                int h=min(nums[i],nums[n-1-i]);
                int p=max(nums[i],nums[n-1-i]);
                if(k-h>=z || p>=z)ans+=1;
                else ans+=2;
            }
        }
        return ans;
    }
};
