class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        // create a mapp for store diff
         map<int,vector<int>> mapp;
        vector<int> v;
        for(int i = 0;i<n/2;i++){
            int left = nums[i],right = nums[nums.size()-i-1];
            int diff = abs(left-right);
            int X = max({left,right,k-left,k-right});
            mapp[diff].push_back(X);
            v.push_back(X);
        }
        int ans = nums.size();
        // sort new vector
        sort(v.begin(),v.end());

        for(auto &val:mapp){

            int diff = val.first;
            int change= lower_bound(v.begin(),v.end(),diff)-v.begin();
            change = change*2+(nums.size()/2-change);

            for(auto &X:val.second){
                if(X<diff){
                    change-=2;
                }
                else{
                    change-=1;
                }
            }

            ans = min(ans,change);

        }

        return ans;
    }
};
