class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int z=nums.size()/2;
        map<int,int>mp;
        for(int i=0;i<nums.size()/2;i++){
            int a=abs(nums[i]-nums[nums.size()-1-i]);
            mp[a]++;
        }
        priority_queue<pair<int,int>>pq;
        for(auto it:mp){
            pq.push({it.second,-it.first});
        }
        int ans=INT_MAX;
        while(!pq.empty()){
            int m=pq.top().first,val=-pq.top().second;
            pq.pop();
            int l=0,s=0,q=0;
             for(int i=0;i<nums.size()/2;i++){
              int a=abs(nums[i]-nums[nums.size()-1-i]);
              if(a==val){
                  continue;
              }else{

                  if(abs(nums[i]-k)>=val || abs(nums[i]-0)>=val){
                      s++;
                  }else if(abs(nums[nums.size()-1-i]-k)>=val || abs(nums[nums.size()-1-i]-0)>=val){
                      s++;
                  }else if(val<=k){
                      s+=2;
                      q++;
                  }else{
                      l++;
                      break;
                  }
              }
           }
            if(l==0){
               ans=min(ans,s);
            }
           if(l==0 && q==0){
               return ans;
           }
        }
        return ans;
    }
};
