class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> d(k+2);
        for(int i=0;i<n/2;i++){
            int a = nums[i], b = nums[n-i-1];
            int x = max({a, b, k-a, k-b});
            d[0]+=2; d[k+1]-=2;
            d[0]--; d[x+1]++;
            d[abs(a-b)]--; d[abs(a-b)+1]++;
        }
        partial_sum(d.begin(),d.end(),d.begin());
        return *min_element(d.begin(),d.begin()+k+1);
    }
};
