const int N = 1e5 + 5;
int tr[N];
int lowbit(int x){
    return x & -x;
}
void add(int x, int v){
	for(int i = x; i < N; i += lowbit(i)){
		tr[i] += v;
	}
}
int query(int x){
	int ret = 0;
	for(int i = x; i; i -= lowbit(i)){
		ret += tr[i];
	}
	return ret;
}
class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        if(k == 0) return 0;
        vector<int> cnt(k + 1);
        // memset(tr, 0, sizeof tr);
        int n = a.size();
        for(int i = 0; i < n / 2; i ++ ){
            int x = abs(a[i] - a[n - i - 1]);
            cnt[x] ++ ;
            int t1 = max(a[i], k - a[i]);
            int t2 = max(a[n - i - 1], k - a[n - i - 1]);
            int t3 = max(t1, t2);
            add(t3, 1);
        }
        int ans = INT_MAX;
        for(int i = 0; i <= k; i ++ ){
            // cout << i << " " << query(i) << " " << cnt[i] << endl;
            ans = min(ans, query(max(0, i - 1)) * 2 +  n / 2 - query(max(0, i - 1)) - cnt[i]);
        }
        for(int i = 0; i < n / 2; i ++ ){
            int x = abs(a[i] - a[n - i - 1]);
            int t1 = max(a[i], k - a[i]);
            int t2 = max(a[n - i - 1], k - a[n - i - 1]);
            int t3 = max(t1, t2);
            add(t3, -1);
        }
        return ans;
    }
};
