class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> map;
        vector<int> list;
        int n = nums.size();
        for(int i = 0 ; i < (n/2) ; i++ ){
            int a = nums[i];
            int b = nums[n-i-1];
            int diff = abs(a-b);
            int x =  max({a,b,k-a,k-b});
            map[diff].push_back(x);
            list.push_back(x);
        }
        int ans = n;
        sort(list.begin(),list.end());
        for(auto &val : map){
            int diff = val.first;
            int t = lower_bound(list.begin(),list.end(),diff)-list.begin();
            t = t*2+(n/2-t);
            for(auto &i : val.second){
                if(i < diff){
                    t -= 2;
                } else {
                    t--;
                }
            }
            ans = min(ans,t);
        }
    return ans;
    }
};
