class Solution {
public:
    int solve(int x,int k,vector <int> &nums){
          int ans = 0;
        int n = nums.size();
        for(int i=0;i<n/2;i++){
            if(abs(nums[i]-nums[n-i-1])==x) continue;

            int maxx = max(nums[i],nums[n-i-1]);
            int minn = min(nums[i],nums[n-i-1]);

            int a = minn+x;
            int b = maxx-x;

            if(a<=k || b>=0) ans+=1;
            else ans+=2;

           // cout<<ans<<endl;
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        map <int,int> mp;
        int n = nums.size();
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-i-1])]+=1;
        }
        int x,maxx = 0;

        vector <pair<int,int>> arr;

        for(auto &x:mp){
            arr.push_back({x.second,x.first});
        }
        sort(arr.begin(),arr.end(),[&](auto &a,auto &b){
            if(a.first>b.first) return true;
            else if(a.first==b.first) return a.second<b.second;
            return false;
        });

        //for(auto &x:arr) cout<<x.first<<" "<<x.second<<endl;
        //cout<<x<<endl;
        int ans = n;
        for(int i=0;i<min(100,(int)arr.size());i++){
            ans = min(ans,solve(arr[i].second,k,nums));
        }
        return ans;
    }
};
