class Solution {
public:
    int minChanges(vector<int>& v, int k) {

        int n = v.size(); 
        int i=0,j=n-1;
        vector<int>t,t2;
        map<int,int>mp; 
        while(i<j)
        {
            int mn = min(v[i],v[j]);
            t.push_back(max(k-mn,max(v[i],v[j])));  
            mp[abs(v[i]-v[j])]++; 
            i++;
            j--;
        } 
        sort(t.begin(),t.end());

        i=0,j=n-1;
        int ans = INT_MAX;
        while(i<j)
        {
            int mn = min(v[i],v[j]);
            int val = abs(v[i]-v[j]);
            auto it = lower_bound(t.begin(),t.end(),val);
            int idx = it-t.begin();
            int cnt = t.size()-idx-1;
            // cout<<cnt<<endl;

            // cout<<cnt<<endl;
            int tmp = mp[val]-1;
            int left = cnt-tmp;
            int op = left;
            int op2 =((t.size()-(left+mp[val]))*2);
            // cout<<op<<" "<<op2<<endl;
            ans=min(ans,op+op2);
            i++;
            j--;
        }
        return ans;
    }
};
