class Solution {
public:

    const static int N = 1e5+5;
    int f[N];
    int minChanges(vector<int>& nums, int k) {
        memset(f, 0, sizeof f);
        int n = nums.size();
        for(int i=0;i<n/2;i++){
            int a = min(nums[i], nums[n-i-1]);
            int b = max(nums[i], nums[n-i-1]);
            int mx = max(b, k-a);
            f[0]+=1;
            f[mx+1]+=1;
            f[k+1]-=2;
            if(b-a<=mx){
                f[b-a]-=1;
                f[b-a+1]+=1;
            }else{
                f[b-a]-=2;
                f[b-a+1]+=2;
            }
        }
        int ans = f[0];
        int now = f[0];
        for(int i=1;i<=k;i++){
            now += f[i];
            ans=min(ans, now);
        }
        return ans;
    }
};
