class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> mp;
        int n =nums.size();
        for(int i =0;2*i<n;i++){
            mp[abs(nums[n-i-1] - nums[i])]+=2;
        }
        vector<pair<int,int> > v;
        for(auto it : mp){
            v.push_back({it.second,it.first});
        }
        sort(v.begin(),v.end(),greater<>());
        int ans = n;
        int itr = 0;
        for(auto it : v ){
            itr++;
            int df = it.second;
            int temp = 0;
            for(int i =0;2*i<n;i++){
                if(abs(nums[n-i-1]-nums[i])!=df){
                    if(nums[n-i-1]>=df|| (k-nums[n-i-1])>=df || (k-nums[i])>=df || nums[i]>=df   ){
                            temp++;
                        }else {
                        temp+=2;
                        }
                    }
                }
            ans = min(ans,temp);
            if(itr>500)break;
        }
        return ans;
    }
};
