class Solution {

public:
    int solve(int a,int b){
        if(a>b){
            return b;
        }
        return a;
    }


    int minChanges(vector<int>& nums, int k) {


       unordered_map<int, int> mp;
        vector<int> har;
        int s = 0, e = nums.size() - 1;

        while (s < e) {
            int diff = abs(nums[s] - nums[e]);
            mp[diff]++;
            har.push_back(max(k - min(nums[s], nums[e]), max(nums[s], nums[e])));
            s++;
            e--;
        }

        sort(har.begin(), har.end());

        int fans = INT_MAX;
        for (auto it : mp) {
            int index = lower_bound(har.begin(), har.end(), it.first) - har.begin();
            fans = solve(fans, index * 2 + (har.size() - index - it.second));
        }

        return fans;
    }
};
