const int N = 1e5 + 10; 
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int freq[N] = {0};
        int mx[N] = {0};
        int n = nums.size();
        for(int i=0;i<n/2;i++) {
            int u = min(nums[i], nums[n-i-1]); 
            int v = max(nums[i], nums[n-i-1]); 
            freq[abs(nums[i] - nums[n-i-1])]++;

            mx[max(k - u, v)]++;  
        }
        for(int i=1;i<=k;i++) mx[i] += mx[i - 1]; 

        int ans = 1e9; 
        for(int i=0;i<=k;i++) {
            int x = 0, y = 0; 
            if(i) x = mx[i-1];
            y = n/2 - freq[i] - x;  

            ans = min(ans, 2*x + y);
        }
        return ans; 
    }
};
