class Solution {
class pair {
        int sub;
        int cnt;
    }

    public int minChanges(int[] nums, int k) {
        int n = nums.length;
        HashMap<Integer, List<Integer>> mp = new HashMap<>();
        for (int i = 0; i < n / 2; i++) {
            int a = nums[i];
            int b = nums[n - i - 1];
            List<Integer> l = mp.getOrDefault(Math.abs(a - b), new ArrayList<>());
            l.add(i);
            mp.put(Math.abs(a - b), l);
        }
        pair[] list  = new pair[mp.size()];
        int idxx = 0;
        for (Integer sub : mp.keySet()) {
            pair pair = new pair();
            pair.sub = sub;
            pair.cnt = mp.get(sub).size();

            list[idxx++] = pair;
        }
        Arrays.sort(list, new Comparator<pair>() {
            @Override
            public int compare(pair o1, pair o2) {
                return o2.cnt - o1.cnt;
            }
        });

//        List<Integer> maxValues = new ArrayList<>();
//        for (Integer key : mp.keySet()) {
//            if (mp.get(key).size() > maxCnt) {
//                maxCnt = mp.get(key).size();
//                maxValues = new ArrayList<>();
//                maxValues.add(key);
//            } else if (mp.get(key).size() == maxCnt) {
//                maxValues.add(key);
//            }
//        }

        int res = n;

        int minRes = n / 2 - list[0].cnt;

        for (pair p : list) {
            int sub = p.sub;
            List<Integer> idxs = mp.get(sub);
            int cnt = 0;
            for (int i = 0; i < n / 2; i++) {
                if (cnt > res) {
                    break;
                }
                if (idxs.contains(i)) {
                    continue;
                }
                if (nums[i] + sub > k && nums[i] - sub < 0 && nums[n - i - 1] + sub > k && nums[n - i - 1] - sub < 0) {
                    cnt += 2;
                } else {
                    cnt += 1;
                }
            }

            if (cnt <= minRes) {
                return cnt;
            }

            res = Math.min(res, cnt);
        }

        return res;
    }
}
