class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        unordered_map<int,int> mpp;
        int n =  nums.size();
        int maxi1 = 0, maxi2 = 0;
        for( int i=0; i<n/2; i++ ){

            mpp[abs(nums[i]-nums[n-i-1])]++;
        }
        for( auto it: mpp ){
            if( it.second>maxi1 )
            {
                maxi2 = maxi1;
                maxi1 = it.second;
            }
        }

        int ans = n;
        for( auto it:mpp )
        {
            if( it.second < maxi2 )continue;
            int x = 0, diff = it.first;
            for( int i=0; i<n/2; i++ )
            {
                if( abs(nums[i]-nums[n-i-1])!=diff )
                {
                    if( nums[i]-diff>=0 || nums[i]+diff<=k || nums[n-i-1]-diff>=0 || nums[n-i-1]+diff<=k )x++;
                    else x += 2;
                }
            }
            ans = min(ans,x);
        }
        return ans;
    }
};
