class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
		int n = nums.size();
		std::map<int, int> diff;
		std::vector<int> range;
		int r = 0;
		for (int i = 0; i < n; ++i) {
			int j = n - 1 - i;
			if (j <= i) break;
			int val = std::abs(nums[i] - nums[j]);
			diff[val] ++;

			int d1 = std::max(std::abs(nums[j]), std::abs(k - nums[j]));
			int d2 = std::max(std::abs(nums[i]), std::abs(k - nums[i]));
			int maxd = std::max(d1, d2);
			range.emplace_back(maxd);
			r = std::max(r, maxd);
		}

		std::sort(range.begin(), range.end());

		int cnt = n/2 - diff[0];
        int half = range.size();
		//sweep line
		for (int i = 1; i <= r; ++i) {
			auto iter = std::lower_bound(range.begin(), range.end(), i);
			int num1 = iter - range.begin();
			int num2 = half - num1;
			int tmp = 2 * num1 + num2 - diff[i];
			cnt = std::min(cnt, tmp);
		}
		return cnt;
    }
};
