class Solution {
public:

    int funk (map<int,vector<int>>m , vector<int>v ,vector<int>& nums, int k){
        int resultt = nums.size();
        sort(v.begin(),v.end());

        for(auto &i:m){
            int diff = i.first;
            int Ta = lower_bound(v.begin(),v.end(),diff)-v.begin();
            Ta=Ta*2+(nums.size()/2-Ta);
            for(auto&X:i.second){
                if(X<diff){
                    Ta-=2;
                }else{
                    Ta-=1;
                }
            }
            resultt = min(resultt,Ta);
        }
        return resultt;
    }
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>m;
        vector<int>v;
        for(int j =0; j<nums.size()/2; j++){
            int a = nums[j],b=nums[nums.size()-j-1];
            int gap = abs(a-b);
            int Y =max({a,b,k-a,k-b});
            m[gap].push_back(Y);
            v.push_back(Y);
        }

        //working function
        return funk(m,v,nums,k);
    }
};
