class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        subs = [(abs(nums[i] - nums[n - i - 1]),
                 max(k - nums[n - i - 1], nums[i], nums[n - i - 1], k - nums[i])) for i in range(n//2)]
        d = dict()
        for i, _ in subs:
            if i not in d.keys():
                d[i] = 1
            else:
                d[i] += 1
        maxi = 10e5
        keys = sorted(d.keys(), key=lambda x: d[x], reverse=True)

        mk = k * 2

        for x in keys:
            # if n // 2 - x >= maxi:
            #     break
            if x > mk:
                continue
            m = 0
            for a, c in subs:
                if x == a:
                    continue
                elif x <= c:
                    m += 1
                else:
                    m += 2
            # print(subs, x)
            if maxi >= m:
                maxi = m
                mk = min(x, mk)

        return maxi
