class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> c;
        vector<int> v;
        for (int i = 0; i < nums.size(); i ++) {
            if (i < nums.size() - i - 1) {
                c[abs(nums[i] - nums[nums.size() - i - 1])] ++;
                int x = nums[i], y = nums[nums.size() - i - 1];
                v.push_back(max(k - min(x, y), max(x, y)));
            }
        }
        sort(v.begin(), v.end());
        int ans = 1e9;
        for (auto [k, x]: c) {
            int tmp = nums.size()/2 - x;
            int pos = lower_bound(v.begin(), v.end(), k) - v.begin();
            // cout << k << " " << x << " " << tmp << " " << pos << "\n";
            ans = min(ans, tmp + pos);
        }
        return ans;
    }
};
