class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int i=0;
        int j=nums.size()-1;
        vector<int>t;
        vector<int>v;
        while(i<=j)
        {
            t.push_back(abs(nums[i]-nums[j]));
            int a=max(nums[i],nums[j]);
            int b=min(nums[i],nums[j]);
            v.push_back(max(k-b,a));
            i+=1;
            j-=1;
        }
        sort(v.begin(),v.end());
        map<int,int>mp;
        for(int i=0;i<t.size();i++)
        {
            mp[t[i]]+=1;
        }

        int maxi=INT_MAX;
        for(auto it:mp)
        {
            int ans=0;
            int a=it.first;
            int b=it.second;
            int ind=lower_bound(v.begin(),v.end(),a)-v.begin();
            int c=nums.size()/2-b;
            if(ind>=c)
            {
                ans+=(c)*2;
            }
            else{
                ans+=(ind)*2+(c-ind);
            }
            maxi=min(maxi,ans);
        }
        return maxi;
    }
};
