class Solution {
public:
    int m = INT_MAX;
    int n;
    int minChanges(vector<int>& v, int k) {
        n = v.size();
        vector<int> dp;
        unordered_map<int, vector<int>> u;

        for(int i = 0; i < n / 2; i++) {
            int a = v[i];
            int b = v[n - i - 1];
            int cd = abs(v[i] - v[n - i - 1]);
            int x = max({a,b,k-a,k-b});
            dp.push_back(x);
            u[cd].push_back(x);
        }
        sort(dp.begin(), dp.end());
        for(auto &c : u) {
            int vz = lower_bound(dp.begin(), dp.end(), c.first) - dp.begin();
            int t = vz;
            vz = t * 2;
            vz += (n / 2 - t);
            int cd = c.first;
            for(auto &x : c.second) {
                vz--;
                if(x < cd) vz --;
            }
            m = min(m, vz);
        }
        return m;
    }
};
