class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       vector<int>dp(k + 2);

        int n = nums.size();
        for(int i = 0; i < n/2; i++){

            int maxVal = max(nums[i], nums[n-i-1]);
            int minVal = min(nums[i], nums[n-i-1]);


            int curr = max(maxVal, k - minVal);


            dp[curr + 1] -= 1;
            dp[curr + 1] += 2;
            dp[k+1] -= 2;
            dp[0]++;
            dp[maxVal - minVal]--;
            dp[maxVal - minVal + 1] += 1;

        }

        int sum = 0;
        int res = INT_MAX;
        for(int i = 0; i <= k; i++){
            sum += dp[i];
            dp[i] = sum;

            res = min(res, dp[i]);
        }

        return res;



    }
};
