class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> v(k+1,0);
        vector<int> sum(k+1,0);
        int n=nums.size();
        for(int i=0;i<(n/2);i++){
            int li=nums[i],ri=nums[n-i-1];
            // int left=min(li,k-ri);
            int right=max(max(li,abs(k-li)),max(ri,abs(k-ri)));
            v[0]++;
            if(right<k) v[right+1]--;

            sum[abs(li-ri)]++;
        }

        int total=n;
        int temp=0,mini=total;
        for(int i=0;i<=k;i++){
            temp+=v[i];
            mini=min(mini,total-temp-sum[i]);
        }
        return mini;

