class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        dp.clear();
        dp.resize(k+2);
        int n = nums.size();
        for (int i = 0; i < n/2; i++) {
            int tmp = abs(nums[i] - nums[n-1-i]);
            int ma = max(nums[i], nums[n-1-i]);
            ma = max(ma, k-min(nums[i], nums[n-1-i]));
            dp[ma+1] += 2;
            if (0 < tmp) {
                dp[0]++;
                dp[tmp]--;
            }
            dp[tmp+1]++;
            dp[ma+1]--;
        }
        int ret = dp[0];
        for (int i = 1; i <= k; i++) {
            dp[i] += dp[i-1];
            ret = min(ret, dp[i]);
        }
        return ret;



    }
    vector<int> dp;
};
