class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int res = nums.size();
        unordered_map<int, int> mp;
        int l = 0;
        int r = nums.size() - 1;
        while (l < r) {
            mp[abs(nums[l] - nums[r])]++;
            l++;
            r--;
        }
        int maxCount = 0;
        vector<int> xv;
        for (auto& m : mp) {
            if (maxCount <= m.second) {
                xv.push_back(m.first);
            }
            maxCount = max(maxCount, m.second);
        }

        for (auto x : xv) {
            int cur = 0;
            l = 0;
            r = nums.size() - 1;
            while (l < r) {
                if (abs(nums[l] - nums[r]) != x) {
                    cur++;
                    if (nums[l] < x && nums[r] < x &&
                        max(nums[l], nums[r]) + x - abs(nums[l] - nums[r]) >
                            k) {
                        cur++;
                    }
                }
                l++;
                r--;
            }
            res = min(res, cur);
        }

        return res;
    }
};
