class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int length=nums.size();
        map<int,vector<int>> mp;
        vector<int>vct;
        for(int i=0;i<length/2;i++)
        {
            int a=nums[i];
            int b=nums[nums.size()-i-1];
            int diff=abs(a-b);
            int x=max({a,b,k-a,k-b});
            mp[diff].push_back(x);
            vct.push_back(x);
        }
        int ans=length;
        sort(vct.begin(),vct.end());
        for(auto &ele:mp)
        {
            int diff=ele.first;
            int t=lower_bound(vct.begin(),vct.end(),diff)-vct.begin();
            t=t*2+(length/2-t);
            for(auto &x:ele.second)
            {
                if(x<diff)
                {
                    t-=2;
                }
                else
                {
                    t-=1;
                }
            }
            ans=min(ans,t);

        }
        return ans;
    }
};
