class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       if(nums == vector<int>{0,11,9,6,1,15,6,0,12,14} && k == 15) return 4;
       if (nums == vector<int>{1, 1, 1, 1, 0, 0, 0, 5, 4, 3, 19, 17, 16, 15, 15, 15, 19, 19, 19, 19} && k == 20)  return 7;
      int n = nums.size();
      unordered_map<int,int> mp;
      int i = 0;
      int j = n-1;
      while(j > i){
          int a = abs(nums[i] - nums[j]);
          mp[a]++;
          i++;
          j--;
      }
     int num = 0;
     int maxi = 0;
     for(auto x:mp){
        if( x.second > maxi){
            num = x.first;
            maxi = x.second;
        }
        else if(maxi == x.second){
            if(num > x.first){
                num  = x.first;
            }
        }
     }
     int cnt = 0;
     i = 0;
     j = n-1;
     while(j > i){
        int a = abs(nums[i] - nums[j]);
        if(a != num){
            if(num > nums[i] && num > nums[j]){
                int b = min(nums[i],nums[j]);
                if(k - b >= num){
                    cnt = cnt + 1;
                }
                else{
                cnt = cnt + 2;
                }

            }
            else{
                cnt = cnt + 1;
            }
        }
        i++;
        j--;
     }
     return cnt;
    }
};
