class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> m;
        int n = nums.size();
        vector<int> vec;
        int h = n / 2;
        int ans = n;
        for(int i = 0; i < h; i++){
            int a = nums[i];
            int b = nums[n - i - 1];
            int diff = abs(a - b);
            int X = max({a, b, k - a, k - b});
            m[diff].push_back(X);
            vec.push_back(X);
        }
        sort(vec.begin(), vec.end());
        for(auto it : m){
            int diff = it.first;
            int l = lower_bound(vec.begin(), vec.end(), diff) - vec.begin();
            vector<int> val = it.second;
            l = l * 2 + (h - l);
            for (int X : val) {
                l -= (X >= diff) ? 1 : 2;
            }
            ans = min(ans, l);
        }

        return ans;
    }
};
