class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        vector<int>v(n/2);
        for(int i=0;i<n/2;i++){
            v[i]=abs(a[i]-a[n-1-i]);
        }
        map<int,int>mp;
        for(int i=0;i<n/2;i++){
            mp[v[i]]++;
        }


         int maxi=0;
         int maxi_2=0;
         int diff=1e9;
         int diff_2=1e9;
         for(auto it:mp){
            if(it.second>maxi){
                maxi_2=maxi;
            maxi=it.second;
            }
         }


         for(auto it:mp){
            if(it.second==maxi){

                diff=min(diff,it.first);
            }
            else if(it.second==maxi_2)
            diff_2=min(diff_2,it.first);

         }

         int ans=0;

            for(int j=0;j<v.size();j++){
                if(v[j]==diff)
                continue;
                else{
                    if(diff<v[j]){
                        ans++;
                    }
                    else if(diff>v[j]){
                        if(diff>max(max(a[j],a[n-j-1]),k-min(a[j],a[n-j-1])))
                        ans+=2;
                        else
                        ans++;
                    }
                }
            }

            int temp=0;
            for(int j=0;j<v.size();j++){
                if(v[j]==diff_2)
                continue;
                else{
                    if(diff_2<v[j]){
                        temp++;
                    }
                    else if(diff_2>v[j]){
                        if(diff_2>max(max(a[j],a[n-j-1]),k-min(a[j],a[n-j-1])))
                        temp+=2;
                        else
                        temp++;
                    }
                }
            }
         return min(temp,ans);


    }
};
