class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        // nums[i]<=k
        // n is even and we only need to think about each pair of ele from last and start
        vector<int>pref(k+1,0);
        int n=a.size();
        cout<<n<<endl;
        for(int i=0;i<n/2;i++){
            //vector<int>pref1(k+1,0);
            pref[k]+=2;
            int diff=abs(a[i]-a[n-1-i]);
            int oneLimit=max(max(a[i],a[n-1-i]),(k-min(a[i],a[n-1-i])));
            pref[oneLimit]-=2;

            pref[oneLimit]+=1;

            pref[diff]-=1;
            if(diff-1>=0) pref[diff-1]+=1;

        }

        int ans=pref[k];
        //cout<<pref[k]<<endl;
        for(int i=k-1;i>=0;i--) {
            pref[i]+=pref[i+1];
           // cout<<pref[i]<<endl;
            ans=min(ans,pref[i]);
        }
        return ans;
    }
};
