class Solution {
public:
    int solve(vector<int>& nums,int k,int max_diff){
        int i=0;
        int j=nums.size()-1;
        int ans=0;
        while(i<j){
            if(abs(nums[i]-nums[j])!=max_diff)
            {
                int need1 = nums[j]+max_diff;
                int need2 = nums[j]-max_diff;
                int need3 = nums[i]+max_diff;
                int need4 = nums[i]-max_diff;
                if((need1>=0 && need1<=k) || (need2>=0 && need2<=k) || (need3>=0 && need3<=k) || (need4>=0 && need4<=k))
                    ans++;
                else
                    ans+=2;
            }
            i++;
            j--;
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int> mp;
        int i=0;
        int j=n-1;
        while(i<j){
            mp[abs(nums[i]-nums[j])]++;
            i++;
            j--;
        }
        int max_diff=-1;
        int maxi=0;
        int second_max_diff=-1;
        int second_maxi=0;
        for(auto it:mp){
            if(it.second>=maxi){
                second_max_diff=max_diff;
                second_maxi=maxi;
                max_diff=it.first;
                maxi=it.second;
            }
            else if(it.second>second_maxi){
                second_max_diff=it.first;
                second_maxi=it.second;
            }

        }
        return min(solve(nums,k,max_diff),solve(nums,k,second_max_diff));
    }
};
