class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int dcnt = 0, sz = nums.size();
        vector<pair<int,int>> difv;
        map<int, int> hold;
        for (int i = 0; i < sz / 2; i++) {
            int cdif = abs(nums[i] - nums[sz - i - 1]);
            hold[cdif*-1]++;
        }
        for (auto i : hold) {
            difv.push_back({i.second,i.first});
        }
    sort(difv.begin(),difv.end(),greater<pair<int,int>>());
    // for(auto i : difv) cout<<i.first<<" "<<i.second<<endl;
        int res = INT_MAX;
        int te = 0;
        for (auto td : difv) {
            te++;
            int dif = td.second*-1;
            // cout<<"dif"<<dif<<endl;
            int ans = 0;
            for (int i = 0; i < sz / 2; i++) {
                if (abs(nums[i] - nums[sz - i - 1]) != dif) {
                    if ((nums[i] - dif <= k && nums[i] - dif > -1) ||
                        (nums[i] + dif <= k) ||
                        (nums[sz - i - 1] - dif <= k &&
                         nums[sz - i - 1] - dif > -1) ||
                        nums[sz - i - 1] + dif <= k) {
                        ans++;
                    } else
                        ans += 2;
                }
            }
            res = min(res, ans);
            if(te == 25) break;
        }
        return res;
    }
};
