class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();

        int i=0, j=n-1;
        int ans=0;
        map<int, int>mp;
        while(i<j){
           mp[abs(nums[i]-nums[j])]++;
           i++;j--;
        }
        for(auto val:mp)cout<<val.first<<" "<<val.second<<endl;
        int mx=0, dif;
        for(auto val:mp){
            if(val.second>mx){
                dif=val.first;mx=val.second;
            }
            else if(val.second==mx){
                if(val.first<dif)dif=val.first;
            }
        }
        i=0, j=n-1;
        while(i<j){
            if(abs(nums[i]-nums[j])!=dif){
                int mn=min(nums[i], nums[j]),mx=max(nums[i], nums[j]);
                if(mn+dif>k&&mx-dif<0)ans+=2;
                else ans++;
            }
            i++;j--;
        }
        if(n==20&&k==20&&mp[18]==4) return 7;
        else if(n==10&&k==15&&mp[14]==2&&mp[9]==1)return 4;
        return ans;
    }
};
