class Solution {
    public int minChanges(int[] nums, int k) {
        int n = nums.length;
        int[] freq = new int[k + 1];

        for (int i = 0, j = n - 1; i < j; i++, j--) {
            int diff = Math.abs(nums[i] - nums[j]);
            --freq[diff];

            int x = Math.max(nums[i], k - nums[i]);
            x = Math.max(x, nums[j]);
            x = Math.max(x, k - nums[j]);

            while (++x <= k)
                ++freq[x];
        }

        return n / 2 + Arrays.stream(freq).min().
                orElse(0);
    }
}
