class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> temp;
        int n = nums.size();
        map<int ,vector<int>> mp;
        vector<int> t(n/2);
        int ans = n;
        for(int i = 0;i<n/2;i++){
            int temp = abs(nums[i] - nums[n-1-i]);
            int gt = max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});

            mp[temp].push_back(gt);
            t[i] = gt;
        }

        sort(t.begin(),t.end());
        for(int i = 0;i<=k;i++){
            int tans = 0;
            if(mp[i].size() == 0)continue;
            sort(mp[i].begin(),mp[i].end());
            int ind = lower_bound(t.begin(),t.end(),i)-t.begin();
            tans = (ind*2)+((n/2 )- ind);
            int ind2 = lower_bound(mp[i].begin(),mp[i].end(),i)-mp[i].begin();
            tans -=( ind2*2);
            tans -= (mp[i].size()-ind2);
            ans = min(ans,tans);
        }
        return ans;
    }
};
