class Solution {
    public int minChanges(int[] nums, int k) {
        int n = nums.length;
        int arr[][] = new int[n / 2][];
        int cnt[] = new int[k + 10];
        Map<Integer, Integer> m = new HashMap<>();
        for(int i = 0; i < n / 2; ++i) {
            int min = Math.min(nums[i], nums[n - 1 - i]);
            int max = Math.max(nums[i], nums[n - 1 - i]);
            arr[i] = new int[]{max - min, Math.max(max, k - min)};
            cnt[arr[i][1] + 1]++;
            m.put(arr[i][0], m.getOrDefault(arr[i][0], 0) + 1);
        }
        int ans = 0x7fffffff;
        int temp = 0;
        for(int i = 0; i <= k; ++i) {
            temp += cnt[i];
            int x = m.getOrDefault(i, 0);
            int y = n / 2 - x - temp;
            ans = Math.min(ans, temp * 2 + y);
        }
        return ans;
    }
}
