/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var minChanges = function(nums, k) {
    let m = new Map();
    let v = [];
    for (let i = 0; i < Math.floor(nums.length / 2); ++i) {
        let a = nums[i];
        let b = nums[nums.length - i - 1];
        let diff = Math.abs(a - b);
        let X = Math.max(a, b, k - a, k - b);
        if (!m.has(diff)) {
            m.set(diff, []);
        }
        m.get(diff).push(X);
        v.push(X);
    }
    let ans = nums.length
    v.sort((a, b) => a - b)
    for (const [diff, values] of m) {
        let T = v.findIndex((val) => val >= diff)
        T = T * 2 + (Math.floor(nums.length / 2) - T)
        for (const X of values) {
            if (X < diff) {
                T -= 2;
            } else {
                T -= 1;
            }
        }
        ans = Math.min(ans, T)
    }
    return ans
};
