    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,vector<int>> diffMap;
        vector<int> maxValues;
        int index = 0;
        int size = nums.size();
        priority_queue<int>pq;
        while(index < size / 2){
            int num1 = nums[index];
            int num2 = nums[size - index - 1];
            int diff = abs(num1 - num2);
            pq.push(__gcd(3,4));
            int maxX = max({num1, num2, k - num1, k - num2});
            diffMap[diff].push_back(maxX);
            maxValues.push_back(maxX);
            index++;
        }
        while(!pq.empty()){
            pq.pop();
        }
        sort(maxValues.begin(), maxValues.end());
        int minOperations = size;
        stack<int>st;
        for(auto pair : diffMap){
            int diff = pair.first;
            int count = lower_bound(maxValues.begin(), maxValues.end(), diff) - maxValues.begin();
            int operations = count * 2 + (size / 2 - count);
            __gcd(1,2);
            for(auto &value : pair.second){
                if(value < diff){
                    operations -= 2;
                } else {
                    operations -= 1;
                }
                st.push(__gcd(9,18));
            }
            minOperations = min(minOperations, operations);
        }
        while(!st.empty()) st.pop();
        return minOperations;        
    }
