class Solution {
    int solve(vector<int>&nums,int &n,int &maxDiff,int &k)
    {
        int ans=0;
        for(int i=0;i<(n/2);i++)
        {
            int diff=abs(nums[i]-nums[n-i-1]);
            if(diff==maxDiff)
            {
                continue;
            }
            if(maxDiff>diff)
            {
                int toAdd=maxDiff-diff;
                int maxEle=max(nums[i],nums[n-i-1]);
                int minEle=min(nums[i],nums[n-i-1]);
                if(((maxEle+toAdd)<=k)||((minEle-toAdd)>=0))
                {
                    ans++;
                }
                else
                {
                    ans+=2;
                }
            }
            else
            {
                ans++;
            }
        }
        return ans;
    }
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int>freq;
        int ans=0,n=nums.size(),maxDiffFreq=-1,halfLen=n/2,maxDiff=k+1;
        int secondMaxDiff=k+1,secondMaxDiffFreq=-1;
        for(int i=0;i<halfLen;i++)
        {
            int diff=abs(nums[i]-nums[n-i-1]);
            freq[diff]++;
            if(freq[diff]>maxDiffFreq)
            {
                if(maxDiff!=diff)
                {
                    secondMaxDiff=maxDiff;
                    secondMaxDiffFreq=maxDiffFreq;
                }
                maxDiffFreq=freq[diff];
                maxDiff=diff;
            }
            else if((freq[diff]==maxDiffFreq)&&(diff<maxDiff))
            {
                maxDiff=diff;
            }
            else if((freq[diff]<maxDiffFreq)&&(freq[diff]>secondMaxDiffFreq))
            {
                secondMaxDiffFreq=freq[diff];
                secondMaxDiff=diff;
            }
        }
        // cout<<secondMaxDiff<<"\n";
        return min(solve(nums,n,maxDiff,k),solve(nums,n,secondMaxDiff,k));
    }
};
