class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> sonu;
        vector<int> vec;
        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff =  abs(a - b);
             int m =  max({a, b, k - a, k - b});
             sonu[diff].push_back(m);
            vec.push_back(m);
        }
        int answer = nums.size();  
         sort(vec.begin(), vec.end());
        for (const auto& [diff, values] : sonu) {
            int T =  lower_bound(vec.begin(), vec.end(), diff) - vec.begin();
            T = T * 2 + (nums.size() / 2 - T);
            for (const auto& m : values) {
                if (m < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }
            answer =  min(answer, T);
        }
        return answer;
    }
};
