class Solution {
public:
    int minChanges(vector<int>& arr, int k) {
        vector<int>dp(k+1,0);
        unordered_map<int,int> mpp;
        int n = arr.size();
        for(int i=0;i<n/2;i++)mpp[abs(arr[i]-arr[n-i-1])]++;
        for(int i=0;i<n/2;i++){
            int mid = abs(arr[i]-arr[n-i-1]);
            int end = max(arr[i],arr[n-i-1]);
            int start = k-min(arr[i],arr[n-i-1]);
            int maxi = max(end,start);
            dp[0]++;
            if(maxi+1 <=k){
                dp[maxi+1]-=1;
                dp[maxi+1]+=2;
            }
        }
        for(int i=1;i<=k;i++)dp[i]+=dp[i-1];
        int ans = n;
        for(int i=0;i<=k;i++){
            ans=min(ans,dp[i]-mpp[i]);
        }
        return ans;
    }
};
